// Category → Group mapping (edit this to change auto-assignments)
const CATEGORY_TO_GROUPS = {
  "All": ["Billing", "Care & Growth", "Clinical Outcome", "HR", "IT/Engineering", "Management", "Product/Solutions"],
  "Care Team": ["Care & Growth", "Clinical Outcome"],
  "C&G": ["Care & Growth"],
  "CO": ["Clinical Outcome"]
};

// Get all groups from the API
const groups = $('Get All Groups').all().map(i => i.json);

// Find unique group names from the mapping
const targetGroupNames = new Set();
for (const groupList of Object.values(CATEGORY_TO_GROUPS)) {
  groupList.forEach(name => targetGroupNames.add(name));
}

// Build URLs for group detail fetches
const output = [];
for (const groupName of targetGroupNames) {
  const group = groups.find(g => g.name === groupName);
  if (group) {
    output.push({
      json: {
        groupId: String(group.id),
        groupName: group.name,
        url: `https://ihealthlabs.talentlms.com/api/v1/groups/id:${group.id}`
      }
    });
  }
}

if (output.length === 0) {
  throw new Error('No matching groups found in TalentLMS. Check group names in mapping.');
}

return output;

// Category → Group mapping (must match Build Group Detail URLs node)
const CATEGORY_TO_GROUPS = {
  "All": ["Billing", "Care & Growth", "Clinical Outcome", "HR", "IT/Engineering", "Management", "Product/Solutions"],
  "Care Team": ["Care & Growth", "Clinical Outcome"],
  "C&G": ["Care & Growth"],
  "CO": ["Clinical Outcome"]
};

// Get data from upstream nodes
const courses = $('Get All Courses').all().map(i => i.json);
const categories = $('Get All Categories').all().map(i => i.json);
const groups = $('Get All Groups').all().map(i => i.json);
const groupDetails = $input.all().map(i => i.json);

// Build lookup maps
const categoryMap = {};
for (const cat of categories) {
  categoryMap[String(cat.id)] = cat.name;
}

const groupNameToId = {};
for (const g of groups) {
  groupNameToId[g.name] = String(g.id);
}

// Build existing assignments set from group details
const existingAssignments = new Set();
for (const gd of groupDetails) {
  const groupId = String(gd.id);
  if (gd.courses && Array.isArray(gd.courses)) {
    for (const course of gd.courses) {
      existingAssignments.add(`${groupId}-${course.id}`);
    }
  }
}

// Find needed assignments
const assignments = [];
const unmapped = [];
const alreadyAssigned = [];

for (const course of courses) {
  const categoryName = categoryMap[String(course.category_id)];

  if (!categoryName) {
    unmapped.push({ courseId: course.id, courseName: course.name, reason: 'Unknown category' });
    continue;
  }

  const targetGroups = CATEGORY_TO_GROUPS[categoryName];
  if (!targetGroups) {
    // Category exists but not in our mapping
    unmapped.push({ courseId: course.id, courseName: course.name, category: categoryName });
    continue;
  }

  for (const groupName of targetGroups) {
    const groupId = groupNameToId[groupName];
    if (!groupId) continue;

    const key = `${groupId}-${course.id}`;
    if (existingAssignments.has(key)) {
      alreadyAssigned.push({ courseName: course.name, groupName });
    } else {
      assignments.push({
        course_id: String(course.id),
        courseName: course.name,
        group_id: groupId,
        groupName
      });
    }
  }
}

// Summary
const unmappedCategories = [...new Set(unmapped.filter(u => u.category).map(u => u.category))];
const summary = {
  totalCourses: courses.length,
  assignmentsNeeded: assignments.length,
  alreadyAssigned: alreadyAssigned.length,
  unmappedCategories,
  unmappedDetails: unmapped
};

if (assignments.length === 0) {
  return [{ json: { hasAssignments: false, summary } }];
}

return assignments.map((a, i) => ({
  json: {
    ...a,
    hasAssignments: true,
    ...(i === 0 ? { _summary: summary } : {})
  }
}));
