// 简化版：只返回最重要的信息
const data = $input.first().json.data;

if (!data) {
  return {
    json: {
      error: "Patient not found",
      patientId: $input.first().json.patientId
    }
  };
}

// 计算年龄
const calculateAge = (birthday) => {
  const today = new Date();
  const birthDate = new Date(birthday);
  let age = today.getFullYear() - birthDate.getFullYear();
  const monthDiff = today.getMonth() - birthDate.getMonth();
  if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
    age--;
  }
  return age;
};

// 格式化日期
const formatDate = (dateString) => {
  if (!dateString) return null;
  return new Date(dateString).toLocaleDateString('en-US');
};

const result = {
  // 基本信息
  patientId: data.id,
  name: `${data.profile.firstName} ${data.profile.lastName}`,
  firstName: data.profile.firstName,
  lastName: data.profile.lastName,
  birthday: data.profile.birthday,
  age: calculateAge(data.profile.birthday),
  gender: data.profile.gender,

  // 联系方式
  phone: data.phone?.[0]?.number || null,
  address: data.address?.[0] ? 
    `${data.address[0].streetName}, ${data.address[0].city}, ${data.address[0].state} ${data.address[0].postCode}` : null,

  // 保险
  primaryInsurance: data.profile.insuranceProvider,
  secondaryInsurance: data.profile.insuranceProvider2,
  memberId: data.profile.memberId,

  // 诊所和医生
  clinic: {
    name: data.clinic?.businessName,
    location: `${data.clinic?.city}, ${data.clinic?.state}`
  },
  doctor: data.profile.doctorUser ? 
    `Dr. ${data.profile.doctorUser.firstName} ${data.profile.doctorUser.lastName}` : null,

  // 护理团队
  assignedRD: data.assignedToRDUser ? 
    `${data.assignedToRDUser.firstName} ${data.assignedToRDUser.lastName} (${data.assignedToRDUser.title})` : null,
  assignedCA: data.assignedToCAUser ? 
    `${data.assignedToCAUser.firstName} ${data.assignedToCAUser.lastName} (${data.assignedToCAUser.title})` : null,

  // 诊断（简化）
  diagnoses: data.healthConditions?.map(d => `${d.code}: ${d.condition}`).join('; ') || null,
  primaryDiagnosis: data.healthConditions?.[0] ? 
    `${data.healthConditions[0].code}: ${data.healthConditions[0].condition}` : null,

  // 项目
  programs: data.patientListInfo?.programs?.map(p => p.category).join(', ') || null,
  enrollmentStatus: data.patientListInfo?.enrolledStatus,
  enrollmentDate: formatDate(data.patientListInfo?.enrollmentDate),

  // 本月计费状态
  currentMonthBillable: {
    eligible: data.monthlyBillableStatistic?.eligible || false,
    totalTime: data.monthlyBillableStatistic?.totalTime || 0,
    measurementDays: data.monthlyBillableStatistic?.measurementDays || 0,
    month: data.monthlyBillableStatistic?.monthOfYear,
    timeEligible: data.monthlyBillableStatistic?.timeEligible || false,
    measurementEligible: data.monthlyBillableStatistic?.measurementEligible
  },

  // 设备信息
  hasBloodPressureDevice: !!data.bpDeviceInfo?.recentUsedDeviceMacId,
  bpLastUsed: formatDate(data.bpDeviceInfo?.recentUsedTime),

  // 最近血压数据
  recentBP: data.bpStatData ? 
    `${data.bpStatData.sbp}/${data.bpStatData.dbp} (${data.bpStatData.count} readings)` : null,

  // 元数据
  medicalRecordNumber: data.medicalRecordId,
  patientComplexity: data.complexity,
  lastUpdated: formatDate(data.updatedAt)
};

return { json: result };
